import React, { useEffect, useState } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';

function CardDetails() {
    const { id } = useParams(); // Retrieves the card ID from the URL
    const history = useHistory(); // Used for programmatic navigation
    const [card, setCard] = useState(null); // State to hold card data
    const [loading, setLoading] = useState(true); // State to manage loading status
    const [error, setError] = useState(''); // State to hold any error message

    // Fetch card details when the component mounts or the id changes
    useEffect(() => {
        fetch(`http://localhost:5001/api/cards/${id}`)
            .then(response => {
                if (response.ok) return response.json();
                throw new Error('Network response was not ok.');
            })
            .then(data => {
                setCard(data.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching card details:', error);
                setError('Failed to fetch card details.');
                setLoading(false);
            });
    }, [id]);

    if (loading) {
        return <p>Loading...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (!card) {
        return <p>No card found.</p>;
    }

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <img src={card.image_url} alt={card.name} style={{ maxWidth: '300px', borderRadius: '10px' }} />
            <h1>{card.name}</h1>
            <p>Type: {card.type}</p>
            <p>Rarity: {card.rarity}</p>
            <p>Set: {card.set_name}</p>
            <p>{card.text || 'No description available.'}</p>
            <button onClick={() => history.goBack()} style={{ marginRight: '10px' }}>Go Back</button>
            <Link to="/" style={{ textDecoration: 'none', color: 'blue' }}>Back to Search</Link>
        </div>
    );
}

export default CardDetails;
