import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom'; // Replace useHistory with useNavigate

function CardDetails() {
    const { id } = useParams();
    const navigate = useNavigate(); // useNavigate instead of useHistory
    const [card, setCard] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetch(`http://localhost:5001/api/cards/${id}`)
            .then(response => response.json())
            .then(data => {
                setCard(data.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching card details:', error);
                setError('Failed to fetch card details.');
                setLoading(false);
            });
    }, [id]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;
    if (!card) return <p>No card found.</p>;

    return (
        <div>
            <img src={card.image_url} alt={card.name} style={{ maxWidth: '300px', borderRadius: '10px' }} />
            <h1>{card.name}</h1>
            <p>Type: {card.type}</p>
            <p>Rarity: {card.rarity}</p>
            <p>Set: {card.set_name}</p>
            <p>{card.text || 'No description available.'}</p>
            <button onClick={() => navigate(-1)}>Go Back</button>
            <Link to="/">Back to Search</Link>
        </div>
    );
}

export default CardDetails;
