import React, { useState, useEffect } from 'react'; // Ensure these hooks are imported
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
function CardList({ cards }) {
    const [validCards, setValidCards] = useState(cards);

    useEffect(() => {
        setValidCards(cards);
    }, [cards]);

    const handleImageError = (index) => {
        setValidCards(prevCards => prevCards.filter((_, i) => i !== index));
    };

    if (!validCards.length) {
        return <p>No cards found with images.</p>;
    }

    return (
        <div id="search-results" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
            {validCards.map((card, index) => (
                <Link key={card.id} to={`/card/${card.id}`}> {/* Assuming card.id is the unique identifier */}
                    <div style={{ margin: '10px', border: '1px solid #ccc', borderRadius: '8px', padding: '16px', textAlign: 'center' }}>
                        <img 
                            src={card.image_url} 
                            alt={card.name} 
                            onError={() => handleImageError(index)}
                        />
                        <p>{card.name}</p>
                        <p>{card.mana_cost || 'No mana cost'}</p>
                    </div>
                </Link>
            ))}
        </div>
    );
}

CardList.propTypes = {
    cards: PropTypes.array.isRequired
};

export default CardList;
